/*Function definition -------------------------------------------------------*/
void GPIO_EXTI_Callback(uint8_t pinNumber){
	if(pinNumber == 11){
		count++;
		LCD_Clear();
		LCD_Printf(0, 0, "IT EXTERNA");
		LCD_Printf(0, 1, "COUNT->%d",count);
		GPIOX_ODR(LED) ^= 1U;
	}

}
