/*Function definition -------------------------------------------------------*/
void USART1_PINConfig(void){
	//PA9 y PA10
	RCC->APB2ENR |= GPIOX_CLOCK(USART1_TX) | GPIOX_CLOCK(USART1_RX);
	//PA9->TX
	GPIOX_MODE(MODE_PP_ALTER_50MHZ, USART1_TX);
	//PA10->RX
	GPIOX_MODE(MODE_FF_DIGITAL_INPUT, USART1_RX);

	return;
}

void USART1_Init(void){
	//habilitar el reloj
	RCC->APB2ENR |= RCC_APB2ENR_USART1EN;
	//configurar los parametros de comunicion
	USART1->CR1 = 0;
	//Paridad
	USART1->CR1 |= USART_CR1_PCE;	//HABILITA LA PARIDAD

	//bits de parada
	//USART1->CR2 |= USART_CR2_STOP_1;	//2 bits de parada
	//velocidad baudios
	/*
	 * Tx/ Rx baud = fCK / (16*USARTDIV)
	 * USARTDIV = [DIV_Mantisa, DIV_Fraction]
	 * USARTDIV = 8MHZ / (16 * 115200)
	 * USARTDIV = 4.3402
	 * DIV_Mantisa = 4
	 * DIV_Fraction = 0.3402 * 16 = 5.444 (Se redondea al entero mas cercano)
	 */
	USART1->BRR = (4U<<4) | 5U;
	//tamÃ±o de datos

	//habilitar la T y R

	USART1->CR1 |= USART_CR1_TE;
	USART1->CR1 |= USART_CR1_RE;
	//habilitar el USART1
	USART1->CR1 |= USART_CR1_UE;			//habilitar el USART1
	return;
}

/*****************************************************************/

int __io_putchar(int ch){
	uint8_t c = ch & 0xFFU;
	USART_SendData(USART1, &c, 1);
	return ch;
}
